function showInputs(e) {
    document.getElementById("input-name-".concat(e)).style.display = 'inline';
    document.getElementById("input-lat-".concat(e)).style.display = 'inline';
    document.getElementById("input-lon-".concat(e)).style.display = 'inline';
    document.getElementById("span-name-".concat(e)).style.display = 'none';
    document.getElementById("span-lat-".concat(e)).style.display = 'none';
    document.getElementById("span-lon-".concat(e)).style.display = 'none';
    document.getElementById("edit-".concat(e)).style.display = 'none';
    document.getElementById("apply-".concat(e)).style.display = 'inline';
}

function hideInputs(x) {
    document.getElementById("input-name-".concat(x)).style.display = 'none';
    document.getElementById("input-lat-".concat(x)).style.display = 'none';
    document.getElementById("input-lon-".concat(x)).style.display = 'none';
    document.getElementById("span-name-".concat(x)).style.display = 'inline';
    document.getElementById("span-lat-".concat(x)).style.display = 'inline';
    document.getElementById("span-lon-".concat(x)).style.display = 'inline';
    document.getElementById("edit-".concat(x)).style.display = 'inline';
    document.getElementById("apply-".concat(x)).style.display = 'none';

    document.getElementById("span-name-".concat(x)).innerHTML = document.getElementById("input-name-".concat(x)).value
    document.getElementById("span-lat-".concat(x)).innerHTML = document.getElementById("input-lat-".concat(x)).value
    document.getElementById("span-lon-".concat(x)).innerHTML = document.getElementById("input-lon-".concat(x)).value

    var t = {}
    t["salonId"] = $("#input-id-".concat(x)).val();
    t["latitude"] = $("#input-lat-".concat(x)).val();
    t["name"] = $("#input-name-".concat(x)).val();
    t["longitude"] = $("#input-lon-".concat(x)).val();

    $.ajax({
        type: "POST",
        url: "/editSalon",
        data: JSON.stringify(t),
        contentType: "application/json",
        dataType: 'json',
        beforeSend: function () {
            console.log("before request")
        },
        success: function (response) {

            console.log("success");
        }

    });
}

function deleteEntry(x) {
    var t = {}
    t["salonId"] = $("#input-id-".concat(x)).val();
    t["latitude"] = $("#input-lat-".concat(x)).val();
    t["name"] = $("#input-name-".concat(x)).val();
    t["longitude"] = $("#input-lon-".concat(x)).val();
    var id = t["salonId"]

    $.ajax({
        type: "DELETE",
        url: "/deleteSalon/".concat(id),

        beforeSend: function () {
            console.log("before request")
        },
        success: function (response) {
            window.location.reload(true);

            console.log("success");
        }

    });

}

function redirect() {
    window.location.reload(true);
}

function addSalon() {

    var name = document.getElementById("inputName");
    var lat = document.getElementById("inputLat");
    var lon = document.getElementById("inputLon");

    var t = {};
    t["name"] = name.value;
    t["latitude"] = lat.value;
    t["longitude"] = lon.value;

    $.ajax({
        type: "POST",
        url: "/addSalon",
        data: JSON.stringify(t),
        contentType: "application/json",
        beforeSend: function () {
            console.log("before request")
        },
        success: function (response) {

            console.log("success");
        }

    });
}

function showLogoutModal() {
    $(document).ready(function () {
        $("#logoutModal").modal("show");
    });
}

